INSERT INTO `productinfo` VALUES (17, 19, 0, 1, '景德镇九段全手工手绘内绘瓜果杯', '特价热卖，有多种图案款式选择，存货不多，拍前请联系，极具实用与收藏价值', '<p><img alt="" width="618" height="494" src="/upload/other/3367047097.jpg" /><img alt="" width="640" height="464" src="/upload/other/3367047211.jpg" /><img alt="" width="640" height="495" src="/upload/other/3367047442.jpg" /><img alt="内绘杯1.jpg" width="600" height="572" src="/upload/other/33498987110.jpg" /></p>\r\n<p><br />\r\n&nbsp;</p>', '', 399.00, 1, 1, '2012-04-21 14:34:57', '2012-05-11 10:56:31', '0000-00-00 00:00:00', 0, 0, 0);
INSERT INTO `productinfo` VALUES (94, 19, 0, 1, '阁仿古青花釉里红缠枝盖碗', '景德镇全手工手绘青花釉里红壶，盖碗，品杯，器型典雅大方，青花发色纯正，釉里红红艳润滑，釉色莹润，全完美品，无瑕疵！\r\n', '<input src="/upload/other/3367079546.jpg" width="700" height="502" type="image" /><input src="/upload/other/3367079582.jpg" width="700" height="473" type="image" /><img alt="" width="700" height="466" src="/upload/other/3367079763.jpg" /><img alt="" width="700" height="611" src="/upload/other/3367079906.jpg" /><img alt="" width="700" height="618" src="/upload/other/3367079951.jpg" /><br />\r\n<br />\r\n<p><strong><span style="font-size: 18px">容量规格：壶：180--220cc，</span></strong><strong><span style="font-size: 18px"> 盖碗：150cc左右，</span></strong><strong><span style="font-size: 18px"> 品杯：40-70cc左右</span></strong></p>\r\n<br />', '', 580.00, 1, 1, '2012-05-11 11:47:14', '2012-05-11 11:48:40', '0000-00-00 00:00:00', 0, 0, 0);
INSERT INTO `productinfo` VALUES (18, 4, 0, 1, '谷仓祁门红茶红毫芽150克单罐', '祁红特级毫芽蓝罐，是公司最新推出的一款包装茶，两罐可送公司精美环保手提袋，送礼简洁实惠大方！ 祁红工夫红茶条索紧秀，锋苗好，色泽乌黑泛灰光；内质香气浓郁高长，似蜜糖香，又蕴藏有兰花香，汤色红艳，滋味醇厚，回味隽永，叶底嫩软红亮', '<p><strong><img alt="" width="1000" height="666" src="/upload/other/3366980139.jpg" /><br />\r\n<img alt="" width="1000" height="666" src="/upload/other/3366980345.jpg" /><br />\r\n祁门工夫红茶简称祁红，产于安徽省祁门县一带，是我国传统工夫红茶中的珍品，有着百余年的生产历史。以外形苗秀，色有&ldquo;宝光&rdquo;和香气浓郁而著称。自1875年问世以来，享誉国际市场，与印度大吉岭、斯里兰卡乌伐红茶并称为世界三大高香红茶。在国际最大的红茶市场&mdash;伦敦，祁红被誉为&ldquo;群芳最&rdquo;和&ldquo;茶中英豪&rdquo;。<br />\r\n<br />\r\n外形: ****&nbsp;&nbsp; 条索紧细，色润泽，锋毫显露 <br />\r\n汤色: *****&nbsp; 红艳明亮,金圈明显<br />\r\n香气: ****&nbsp;&nbsp; 高醇，鲜嫩，有独特的&ldquo;祁门香&rdquo;<br />\r\n滋味: ****&nbsp;&nbsp; 醇厚，鲜甜<br />\r\n叶底: ***&nbsp;&nbsp;&nbsp; 嫩叶匀整，色红艳<br />\r\n</strong></p>', '', 240.00, 1, 1, '2012-04-21 15:03:21', '2012-05-12 18:47:34', '0000-00-00 00:00:00', 0, 0, 0);
INSERT INTO `productinfo` VALUES (91, 6, 0, 1, '谷仓祁门红茶红一级150克单罐', '祁红一级经典红罐150克装是实体店铺销售最好，最受消费者欢迎的一款包装茶.祁红工夫红茶条索紧秀，锋苗好，色泽乌黑泛灰光；内质香气浓郁高长，似蜜糖香，又蕴藏有兰花香，汤色红艳，滋味醇厚，回味隽永，叶底嫩软红亮。', '<strong>祁门工夫红茶简称祁红，产于安徽省祁门县一带，是我国传统工夫红茶中的珍品，有着百余年的生产历史。以外形苗秀，色有&ldquo;宝光&rdquo;和香气浓郁而著称。自1875年问世以来，享誉国际市场，与印度大吉岭、斯里兰卡乌伐红茶并称为世界三大高香红茶。在国际最大的红茶市场&mdash;伦敦，祁红被誉为&ldquo;群芳最&rdquo;和&ldquo;茶中英豪&rdquo;。</strong><br />\r\n精美铁罐包装纯正高品质祁门红茶:一级&nbsp;,所有茶叶的原料均来自祁门县境内优质祁红茶园出产区. 坚持传统的祁门红茶加工工艺. <br />\r\n有机茶,低农残,无公害茶叶: 本公司下属工厂获得德国BCS有机认证. 茶叶检测报告齐全,所有茶叶均达到出口欧盟标准. 坚持新品祁门红茶的创新: 今年我公司采用清明前优质茶芽试制新品&quot;清明红毫香&quot;;采用谷雨前精选一芽一叶试制新品&quot;谷雨红芽&quot;.&nbsp;新品茶叶的推出受到了茶叶界专家们的肯定. 新品祁门红茶的推出旨在为消费者提供高品质祁门红茶.<br />\r\n<img alt="" src="/upload/other/3367130671.jpg" /><img alt="" width="310" height="306" src="/upload/other/3367130736.jpg" /><br />\r\n<br />\r\n<br />\r\n&nbsp;', '', 105.00, 1, 1, '2012-05-10 11:47:26', '2012-05-11 13:33:27', '0000-00-00 00:00:00', 0, 0, 0);
INSERT INTO `productinfo` VALUES (92, 5, 0, 1, '谷仓祁门红茶特级150克黑罐', '经典黑罐，祁红特级经典黑罐150克装是网络店铺销售最好，最受消费者欢迎的一款包装茶，祁红工夫红茶条索紧秀，锋苗好，色泽乌黑泛灰光；内质香气浓郁高长，似蜜糖香，又蕴藏有兰花香，汤色红艳，滋味醇厚，回味隽永，叶底嫩软红亮。 \r\n', '<p><img alt="" src="/upload/other/3367136366.jpg" /><input src="/upload/other/3366301732.jpg" width="750" height="411" type="image" /></p>\r\n<p>纯正祁门工夫红茶简称祁红，产于安徽省祁门县一带，是我国传统工夫红茶中的珍品，有着百余年的生产历史。以外形苗秀，色有&ldquo;宝光&rdquo;和香气浓郁而著称。自1875年问世以来，享誉国际市场，与印度大吉岭、斯里兰卡乌伐红茶并称为世界三大高香红茶。在国际最大的红茶市场&mdash;伦敦，祁红被誉为&ldquo;群芳最&rdquo;和&ldquo;茶中英豪&rdquo;。最近全部使用2012年新茶工夫毫芽包装，确保从里到外的美！精美铁罐包装纯正高品质祁门红茶:特级。所有茶叶的原料均来自祁门县境内优质祁红茶园出产区。坚持传统的祁门红茶加工工艺，有机茶,低农残,无公害茶叶,公司下属工厂获得德国BCS有机认证.茶叶检测报告齐全,所有茶叶均达到出口欧盟标准.&nbsp;</p>', '', 145.00, 1, 1, '2012-05-10 14:04:09', '2012-05-14 16:38:44', '0000-00-00 00:00:00', 0, 0, 1001);
INSERT INTO `productinfo` VALUES (19, 5, 0, 1, '祁红特级150克单罐', '内装纯正祁门工夫红茶礼茶250克，瓷罐为景德镇手绘精品，可收藏，礼盒内配工艺茶勺一把，红茶介绍一份，高档品味，送礼首选.瓷罐花色有梅兰竹菊四款，任意选择。同这系列还有一款125克祁红礼茶的瓷罐礼盒，每款礼盒都配有无纺布手提袋，环保美观', '<p><strong>工夫瓷罐大红礼茶礼盒</strong><br />\r\n<strong><img alt="" width="600" height="381" src="/upload/other/3349921345.jpg" /></strong><br />\r\n<strong>价格：880元/套，</strong><br />\r\n<strong>产品特点： </strong><br />\r\n<strong>规格：50克祁红礼茶*5包/罐</strong><br />\r\n<strong>大款外盒尺寸：长17.5*宽18.5*高16cm</strong><br />\r\n<strong>特点：内装纯正祁门工夫红茶礼茶250克，瓷罐为景德镇手绘精品，可收藏，礼盒内配工艺茶勺一把，红茶介绍一份，高档品味，送礼首选.瓷罐花色有梅兰竹菊四款，任意选择。同这系列还有一款125克祁红礼茶的瓷罐礼盒，每款礼盒都配有无纺布手提袋，环保美观 </strong></p>\r\n<p>&nbsp;</p>', '', 880.00, 0, 1, '2012-04-21 15:09:08', '2012-04-21 15:15:49', '2012-05-10 11:25:22', 0, 0, 0);
INSERT INTO `productinfo` VALUES (20, 2, 0, 1, '手绘青花工夫小瓷罐祁红礼茶礼盒（梅兰竹菊）', '馈赠朋友、领导、亲戚佳品，既实用又有文化底蕴，绝对有面子，是现代白领送礼首选。\r\n\r\n内装纯正祁门工夫红茶礼茶125克，瓷罐为景德镇手绘精品，可收藏，礼盒内配工艺茶勺一把，红茶介绍一份，高档品味，送礼首选.瓷罐花色有梅兰竹菊四款，任意选择。', '<p><strong><img alt="" width="750" height="244" src="/upload/other/3367151794.jpg" />&nbsp;</strong><strong>&nbsp;</strong></p>\r\n<h4><strong>&nbsp;</strong></h4>\r\n<p><img alt="" src="/upload/other/3366984571.jpg" /></p>\r\n<p><img alt="" width="587" height="600" src="/upload/other/3349924721.jpg" /></p>\r\n<p><img alt="" width="656" height="417" src="/upload/other/3349924855.jpg" /><br />\r\n&nbsp;</p>\r\n<p><font color="#660033" size="5"><strong>&nbsp;规格：125g祁红礼茶/套&nbsp; <br />\r\n外盒尺寸：长宽高均为13cm</strong></font></p>', '', 600.00, 1, 1, '2012-04-21 15:14:38', '2012-05-12 20:41:09', '0000-00-00 00:00:00', 0, 0, 0);
